INSERT INTO `met_column` VALUES ('92', '企业文化', 'about', '', '90', '0', '1', '2', '0', '0', '0', '0', '', '', '<p><span style=\"color: rgb(192, 0, 0);\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">核心文化CORE CULTURE</span></strong></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【艾邸使命】建筑人居梦想</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【艾邸愿景】百年地产，中国榜</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">样</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【艾邸精神】求实、创新，每天进</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">一步</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【艾邸作风】做好每个细节</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【艾邸价值观】实现“三个满意”，</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">奉行“三个大于”	【三个满意为】“员工满意、客户满意、社会满意”</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【三个大于为】“人的价值大于物的价</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">值；团队价值大于个人价值；社会价值大于企业价值”</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【品牌主张】美好你的生活</span></p><p><span style=\"color: rgb(192, 0, 0);\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">经营文化MANAGEMENT CULTURE</span></strong></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【经营理念】经营企业就是经营客户</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【利润观】追求持续稳健的赢利能力</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【品牌理念】意识决定品质，品质铸造品牌</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【产品理念】人无我有、人有我新、人新我快</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【营销理念】最好的营销源于对客户的深刻理解</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【质量理念】高标准、零缺陷；砸质量就是砸饭碗	【市场理念】把握市</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">场，引领需求；没有疲软的市场，只有疲软的行动</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【服务理念】服务是我们的天职；迅速反应，创造感动</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【创新观】创新无处不在；创新的主体是每一位员工；创新的目标就是</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">创造效益；创新的途径是率先性模仿并超越</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【管理目标】人人事事有责任，时时处处讲效率</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【管理理念】制度化、规范化，重执行、重监督；管理的难点就是创新的</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">课题</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【团队理念】创造1+1&gt;2的团队价值；创建互补型、学习型、创新型团队【</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">营销理念】最好的营销源于对客户的深刻理解</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【工作观】把工作当成事业，生活将无限美好</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【“三大问题”警示】终端的问题是领导的问题；看不出的问题是最大</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">的问题；重复出现的问题是最可怕的问题</span></p><p><span style=\"color: rgb(192, 0, 0);\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">人才文化TALENTS</span></strong></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【人才观】人才是金科第一资源；尊重知识、尊重人才；人人都有</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">可能成为人才；金科的岗位无贵贱之分；重学历而不唯学历；</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【用人原则】德才兼备者重用；用人之长，避人之短；制度制衡，均</span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">衡绩效；优先选拔内部人才；正确看待失败</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【人才管理准则】以人为本，理性公正，责任到位，有效沟通</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【考评原则】客观性、公正性、有效性；过程考核与结果考核并重</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【激励原则】贡献与回报成正比；业内富有竞争力</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【培养原则】帮助员工培养终生就业能力</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">【竞争原则】赛马与相马相结合</span></p>', '', '1', '0', '2', '', '0', '0', '', 'http://www.smart-alliance.com/images/new/center1_us2.jpg', '1', 'en', '', '0', '0', '', '');
