INSERT INTO `met_column` VALUES ('91', '公司介绍', 'about', '', '90', '0', '1', '1', '0', '0', '0', '0', '', '', '<p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><font face=\"微软雅黑, Microsoft YaHei\">IDED艾邸物业股份有限公司</font></strong></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">艾邸物业自1992年创立以来，以“让社区变得更美好”为使命，奉行“我们的价值源于满意的服务和诚意的链接”的核心价值观，经过近30年的发展，已经成为中国最大的现代物业服务集团企业之一，并长期稳居中国物业管理行业市场化运营领先企业前列、中国物业管理行业综合实力前五强。艾邸物业是中国首批国家一级资质物业服务企业，员工人数约30000人（含分包方），形成了覆盖全国的发展态势，集团以“三精化”网格管理模式，着力打造“物业管理发展”与“社区生态建设”双主航道的发展模式。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">在物业管理业务领域，艾邸物业设立了五大区域及深圳、北京、上海、天津、武汉、南京、苏州、成都、重庆、福州、济南、合肥、南昌、大连、长沙、银川、乌鲁木齐等30余个分支机构，物业服务范围覆盖中国31个省、自治区、直辖市的80余个城市，物业项目约750余个，物业面积逾1.7亿平方米。所服务的物业类型包括：住宅（独立式房屋、多层、高层）和公建（写字楼、商业综合体、政府办公楼、大学、医院、公园、会展中心、工业物流园区等）。</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">在社区生态圈建设领域，艾邸物业秉承包容式（而非攫取式）整合社区资源，共创共享行业社区生态圈价值，通过以物联网和云计算技术打造的集物业服务、社区商务和公共服务于一体的智慧型社区运营平台的全面应用，整合线上商家资源和线下社区资源，将物业管理和社区商务进行深度融合，积极推动社区生态圈的繁荣，并最终促进物业管理的良性发展和社区生活方式的蝶变式进化。</span></p><p style=\"text-align: center\"><img src=\"http://127.0.0.1/upload/201806/1529655484555798.jpg\" data-width=\"900\" width=\"900\" data-height=\"500\" height=\"500\" title=\"网站关键词\" alt=\"网站关键词\"/></p><p><br/></p>', '', '1', '0', '2', '', '0', '0', '', 'http://www.smart-alliance.com/images/new/center1_us1.jpg', '1', 'en', '', '0', '0', '', '');
